import styled from "styled-components";

const CloseButton = styled.button`
  background: black;
  font-size: 3rem;
  position: absolute;
  top: 2rem;
  right: 2rem;
  color: white;
  margin: 0;
  border: 0;
  padding: 0 1rem;
  display: inline-block;
  z-index: 10;
`;

export default CloseButton;
